'''Find number of occurancies for a pattern in a string'''
dna = ''
pattern = 'CCTATCACC'
count = 0
for i in range (len(dna)-len(pattern)+1):
    if dna[i:i+len(pattern)] == pattern:
        count = count + 1
print(count)


'''Find multiple positions of a pattern'''
import re
dna = ''
pattern = 'CCTATCACC'
for m in re.finditer(pattern, dna):
         print('Pattern found', m.start(), m.end())


dna = ''
pattern = 'CCTATCACC'
def find_all(theString, aString):
    count = 0
    i = len(aString)
    x = 0
    while x < len(theString) - (i-1): 
        if theString[x:x+i] == aString:        
            print("Found in position: ", x)
            x=x+i
            count=count+1
        else:
            x=x+1
    return count
print("Number of occurrences: ", find_all(dna, pattern))